mapper.select:
SELECT IF(rno = gno, 0,1) AS step,
tno, rno, gno, reply, replyer, replyDate, modifyDate
FROM tbl_reply
WHERE tno = #{tno}
ORDER BY gno asc, rno asc 
LIMIT #{page.skip}, #{page.size}

mapper.update:
UPDATE tbl_reply 
SET gno = #{gno}
WHERE rno = #{rno}

UPDATE tbl_reply
SET reply = #{reply}, replyer = #{replyer}, modifyDate = now()
WHERE rno = #{rno}
